    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  constructor(props) {
    super(props);
    this.state = {
      account: "0x0",
    };
  }
  render() {
    return (
      <React.Fragment>
        <Navbar account={this.state.account} />
      </React.Fragment>
    );
  }
}

export default App;
